INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES(63, 1, '2012-03-06 23:10:45', '2012-03-06 23:10:45', '<p>To get things going we will need to discuss your project. Providing me with the size, timeframe,  and a general or specific  description of your idea are a good start to a great mural.  </p>\r\n<p>Phone me @ 832-457-0657 or<br />e-mail me: <a href="mailto:david@davidcobbmurals.com">david@davidcobbmurals.com</a></p>', 'Step 1 - Lets Talk', '', 'publish', 'closed', 'closed', '', 'process-step-1', '', '', '2012-03-20 03:46:44', '2012-03-20 03:46:44', '', 0, 'http://localhost:8888/davidcobbmurals/?post_type=mpm-process-steps&#038;p=63', 2, 'mpm-process-steps', '', 0);
